import React, { Component } from 'react';
import '../App.css'
import Square from './Square.js'

class Board extends Component{
    constructor(props){
        super(props)
        this.state = { spaces: ["?", "?", "?", "?", "?", "?", "?", "?", "?"], 
                       treasure: 0
        }
    }

    treasure = () =>{
        let arr = this.state.spaces
        let num = Math.floor(Math.random() * arr.length -1)
        console.log(num)
        return this.setState({treasure: num})
    }

    handleClick = (squareIndex) =>{
        let arr = this.state.spaces
        arr[squareIndex] = "tree"
        this.setState({spaces: arr})
        if(this.state.treasure==squareIndex){
            alert ("you win")
        }
    }
    render(){

        let square = this.state.spaces.map((value, index) => {
        return(
            <Square
                key={index}
                index={index}
                value={value}
                handleClick={this.handleClick}
            />
             )   
        })
        return(
           <div>
                <div id="grid">{square}</div>
             <button onClick={this.treasure}>Start</button>
            </div>
        )
    }
}
export default Board
